import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { detailUser, updateProfile } from '../actions/userActions';
import { Button, Form, Row, Col, Table } from 'react-bootstrap';
import {LinkContainer} from 'react-router-bootstrap'
import { Link } from 'react-router-dom';
import FormContainer from '../components/FormContainer';
import Message from '../components/Message';
import Loader from '../components/Loader';
import {getListOrder} from '../actions/orderAction'
import {FaTimes} from 'react-icons/fa'
const ProfileScreen = ({ history }) => {
  const dispatch = useDispatch();
  const [email, setEmail] = useState('');
  const [name, setName] = useState('');
  const [address, setAddress] = useState('');
  const [district, setDistrict] = useState('');
  const [city, setCity] = useState('');
  const [password, setPassword] = useState('');
  const [passwordNew, setPasswordNew] = useState('');
  const userDetails = useSelector((state) => state.userDetails);
  const { loading, error, user } = userDetails;
  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;
  const userUpdate = useSelector((state) => state.userUpdate);
  const { success,errorUpdate } = userUpdate;
  const orderList=useSelector(state=>state.orderList);
  const {loading:loadingOrder,error:errorOrder,orders}=orderList
  const submitHandler = (e) => {
    e.preventDefault();
    const newInfo={
      name:name,
      email:email,
      password:password,
      passwordNew:passwordNew,
      address:address,
      district:district,
      city:city,
    }
    dispatch(updateProfile(newInfo))
  };
  useEffect(() => {
    if (!userInfo) {
      history.push('/login');
    }
    if (!user || !user.name) {
      dispatch(detailUser(userInfo.id));
      dispatch(getListOrder(userInfo.id));
    } else {
      setName(user.name);
      setEmail(user.email);
      setAddress(user.address);
      setDistrict(user.district);
      setCity(user.city)
    }
  }, [dispatch, userInfo, history, user]);
  return (
    <Row>
      <Col md={3}>
      <h1>Thông tin </h1>
          {error && <Message variant='danger'>{error}</Message>}
          {errorUpdate && <Message variant='danger'>{errorUpdate}</Message>}
          {success && <Message variant='success'>Cập nhật thành công</Message>}
          {loading && <Loader />}
          {user && (
            <>
              <Form onSubmit={submitHandler}>
                <Form.Group>
                  <Form.Label>Tên</Form.Label>
                  <Form.Control
                    type='text'
                    placeholder='Nhập tên'
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    required
                  ></Form.Control>
                </Form.Group>
                <Form.Group>
                  <Form.Label>Email</Form.Label>
                  <Form.Control
                    type='email'
                    placeholder='Nhập email'
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                  ></Form.Control>
                </Form.Group>
                <Form.Group>
                  <Form.Label>Địa chỉ</Form.Label>
                  <Form.Control
                    type='text'
                    placeholder='Nhập địa chỉ'
                    value={address}
                    onChange={(e) => setAddress(e.target.value)}
                    required
                  ></Form.Control>
                </Form.Group>
                <Form.Group>
                  <Form.Label>Quận - huyện</Form.Label>
                  <Form.Control
                    type='text'
                    placeholder='Nhập quận - huyện'
                    value={district}
                    onChange={(e) => setDistrict(e.target.value)}
                    required
                  ></Form.Control>
                </Form.Group>
                <Form.Group>
                  <Form.Label>Thành phố</Form.Label>
                  <Form.Control
                    type='text'
                    placeholder='Nhập thành phố'
                    value={city}
                    onChange={(e) => setCity(e.target.value)}
                    required
                  ></Form.Control>
                </Form.Group>
                <Form.Group>
                  <Form.Label>Mật khẩu hiện tại</Form.Label>
                  <Form.Control
                    
                    type='password'
                    placeholder='Nhập mật khẩu'
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                  ></Form.Control>
                </Form.Group>
                <Form.Group>
                  <Form.Label>Mật khẩu mới</Form.Label>
                  <Form.Control
                    
                    type='password'
                    placeholder='Nhập mật khẩu'
                    value={passwordNew}
                    onChange={(e) => setPasswordNew(e.target.value)}
                  ></Form.Control>
                </Form.Group>

                <Button type='submit' variant='primary'>
                  Cập nhật
                </Button>
              </Form>
            </>
          )}
      </Col>
      <Col md={9}>
        <h1>Danh sách đơn hàng</h1>
        {/* {loadingOrder?<Loader/>:errorOrder?<Message variant="danger">{errorOrder}</Message>:(
          <Table striped bordered hover responsive className='table-sm'>
            <thead>
              <tr>
                <th>ID</th>
                <th>DATE</th>
                <th>TOTAL</th>
                <th>PAID</th>
                <th>DELIVERED</th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              {orders.map(order=>(
                <tr key={order.id}>
                  <td>{order.id}</td>
                  <td>{order.createdAt.substring(0)}</td>
                  <td>{order.totalPrice}</td>
                  <td>{order.isPaid?order.paidAt.substring(0,10):(
                    <FaTimes/>

                  )}</td>
                  <td>{order.isDelivered?order.deliveredAt.substring(0,10):
                    <FaTimes/>
                  }</td>
                  <td>
                    <LinkContainer to={`/order/${order.id}`}>
                      <Button variant='light'>Details</Button>
                    </LinkContainer>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        )} */}
      </Col>
    </Row>
  );
};

export default ProfileScreen;
